html {
  font-family: Abel, sans-serif;
  color: #fff;
}
main {
  background: -moz-linear-gradient(right, #999, #222);
  background: -webkit-gradient(
    linear,
    left top,
    right top,
    from(#999),
    to(#222)
  );
  background: -webkit-linear-gradient(left, #999, #222);
  background: -moz-linear-gradient(left, #999, #222);
  background: linear-gradient(90deg, #999, #222);
}
input[type="radio"] {
  display: none;
}
input[type="radio"][id="open"]:checked ~ #overlay {
  opacity: 0.8;
  z-index: 98;
}
input[type="radio"][id="open"]:checked ~ #info {
  visibility: visible;
  opacity: 1;
  position: fixed;
  top: 50%;
  left: 50%;
  -webkit-transform: translate(-50%, -50%);
  -moz-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
}
label {
  color: #fff;
}
#button,
label:hover {
  color: #999;
  cursor: pointer;
}
#button {
  position: absolute;
  top: 50%;
  left: 50%;
  -webkit-transform: translate(-50%, -50%);
  -moz-transform: translate(-50%, -50%);
  transform: translate(-50%, -50%);
  border: 4px solid;
  padding: 20px;
  border-radius: 5px;
  font-size: 2em;
  -moz-transition: 0.5s;
  -webkit-transition: 0.5s;
  transition: 0.5s;
}
#button:hover {
  color: #9df;
}
